        };

        const layout = {
            xaxis: { 
                title: { text: 'Importance', font: { size: 13, color: '#334155' } },
                tickfont: { size: 11, color: '#64748b' },
                gridcolor: '#e2e8f0',
                gridwidth: 1,
                fixedrange: false
            },
            yaxis: { 
                title: { text: 'Feature', font: { size: 13, color: '#334155' } },
                tickfont: { size: 10, color: '#64748b' },
                gridcolor: '#e2e8f0',
                gridwidth: 1,
                autorange: 'reversed',
                fixedrange: false
            },
            margin: { l: 120, r: 50, t: 30, b: 50 },
            autosize: true,
            paper_bgcolor: 'rgba(0,0,0,0)',
            plot_bgcolor: 'rgba(0,0,0,0)',
            font: { family: 'Inter, -apple-system, BlinkMacSystemFont, sans-serif', size: 12, color: '#1e293b' },
            hovermode: 'closest',
            showlegend: false
        };

        const config = {
            responsive: true,
            displayModeBar: true,
            modeBarButtonsToRemove: ['lasso2d', 'select2d'],
            displaylogo: false,
            toImageButtonOptions: { format: 'png', filename: 'feature-importance', height: 500, width: 900, scale: 2 },
            doubleClick: 'reset',
            autosizable: true
        };

        Plotly.newPlot(container, [trace], layout, config);

        const resizeHandler = function() {
            if (container.offsetWidth > 0) {
                Plotly.Plots.resize(container);
            }
        };

        let resizeTimeout;
        const resizeListener = function() {
            clearTimeout(resizeTimeout);
            resizeTimeout = setTimeout(resizeHandler, 100);
        };
        window.addEventListener('resize', resizeListener);
    }

    function renderAll() {
        renderModelComparison();
        renderFeatureImportance();
    }

    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', loadData);
    } else {
        loadData();
    }
})();

