                                savedCamera = updatedPlotEl.layout.scene.camera;
                                window.savedCamera = savedCamera;
                            } else if (updatedPlotEl._fullLayout && updatedPlotEl._fullLayout.scene && updatedPlotEl._fullLayout.scene.camera) {
