/* Methodology Page - Image Positioning and Styling */

/* Ensure images stay behind text */
.methodology-image-container {
    position: relative;
    z-index: 1;
}

.methodology-image-container img {
    max-width: 100%;
    height: auto;
    object-fit: contain;
}

/* Text containers should be above images */
.methodology-text-container {
    position: relative;
    z-index: 2;
    background: rgba(255, 255, 255, 0.98);
    padding: 20px;
    border-radius: 8px;
}

/* Centered full-width sections */
.methodology-centered-section {
    max-width: 100%;
    margin: 0 auto;
}

.methodology-centered-section .block-style-thirteen {
    max-width: 1200px;
    margin: 0 auto;
}

/* Methodology step cards */
.methodology-step-card {
    background: #fff;
    border-radius: 12px;
    padding: 30px;
    box-shadow: 0px 4px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1), box-shadow 0.6s cubic-bezier(0.4, 0, 0.2, 1);
    height: 100%;
    position: relative;
}

.methodology-step-card:hover {
    transform: translateY(-5px);
    box-shadow: 0px 8px 25px rgba(0, 0, 0, 0.12);
}

.methodology-step-card .numb {
    font-size: 48px;
    font-weight: 700;
    color: #3b82f6;
    margin-bottom: 15px;
}

.methodology-step-card h6 {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 12px;
    color: #1a1a1a;
}

.methodology-step-card span {
    font-size: 15px;
    color: #666;
    line-height: 1.7;
    display: block;
}

