/* Image vertical centering with text */
.methodology-image-row {
    display: flex;
    align-items: center;
    min-height: 450px;
    position: relative;
}

.methodology-image-row .illustration-holder {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
    width: 100%;
}

.methodology-image-row .illustration-holder img {
    max-height: 100%;
    width: auto;
