/* Responsive adjustments */
@media (max-width: 991px) {
    .methodology-image-row {
        min-height: 350px;
    }

    .limitations-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .methodology-text-container {
        margin-top: 30px;
    }
}

@media (max-width: 767px) {
    .methodology-image-row {
        min-height: 300px;
    }

    .methodology-step-card {
        margin-bottom: 20px;
    }
}

@media (max-width: 575px) {
    .limitations-grid {
        grid-template-columns: 1fr;
    }

    .methodology-image-row {
        flex-direction: column;
        min-height: auto;
    }

    .methodology-image-container {
        order: 2;
        margin-top: 20px;
    }

    .methodology-text-container {
        order: 1;
    }
}

